        {
            html += $"<div class=\"row\"> <div class=\"col-4\">&nbsp;</div> <div class=\"col-4 text-center text-white\"> <a style=\"color: rgb(75, 78, 250);overflow-wrap: break-word;\">{key} :</a> </div> <div class=\"col-4\">&nbsp;</div> </div> <div class=\"row\"> <div class=\"col-4\">&nbsp;</div> <div class=\"col-4 text-center text-white\"> <a style=\"overflow-wrap: break-word\">{value}</a> </div> <div class=\"col-4\">&nbsp;</div> </div> <div class=\"rPadding\">&nbsp;</div>";
        }
        return html;
    }
    public static string DirectoryListingItem(string href, string value, bool toUpper, DateTime date, double size)
    {
        string sizeString;
        if (size == 0)
        {
            sizeString = "-";
        }
        else
        {
            sizeString = Math.Round((size/1000), 1) + "K";
        }
        value = toUpper ? value.ToUpper() : value.ToLower();
        return $"<div class=\"row\"> <div class=\"col-6\"> <div> <a href=\"{href}\">{value}</a> </div> </div> <div class=\"col-4\"> <div> <a>{date}</a> </div> </div> <div class=\"col-2\"><div><a>{sizeString}</a></div></div></div>";
    }

